let mongoAdapter = require('./adapters/mongoose');
let sequelizeAdapter = require('./adapters/sequelize');
module.exports = function (oApiConfiguration) {
  return function() {
    let oDriver = null;
    switch (oApiConfiguration.adapter) {
      case "mongoose" :
        oDriver = new mongoAdapter(oApiConfiguration);
        break;
      case "sequelize" :
        oDriver = new sequelizeAdapter(oApiConfiguration);
        break;
      default :
        oDriver = new mongoAdapter(oApiConfiguration);
    }
    return oDriver;
  }();
};
