let Sequelize = require('sequelize');
let sequelize = require('../sqlite-connect');

module.exports = sequelize.define('foo', {
    title: {
      type: Sequelize.STRING
    },
    content: {
      type: Sequelize.STRING
    },
    value: {
      type: Sequelize.INTEGER
    },
    user_id: {
      type: Sequelize.STRING
    },
    created: {
      type: Sequelize.DATE,
      defaultValue: Sequelize.NOW
    }
  },
  {
    freezeTableName: true
  }
);
