/**
 * Truncate Normalizer
 *
 * Strip HTML markup and return n first words
 *
 * @param sInput
 * @param oParameters
 * @returns {string}
 */
module.exports.normalize = function(sInput, oParameters) {
  if (! oParameters) {
    oParameters = {length:  90};
  }

  var aInput = sInput.split(/\r?\n/),
    sOutput = ( (aInput.length !== 0) ?  aInput[0] : sInput);

  return sOutput.replace(/<(?:.|\n)*?>/gm, '').substr(0, oParameters.length);
};