let Sequelize = require('sequelize');

module.exports = new Sequelize('test', 'test', 'test', {
  host: 'localhost',
  dialect: 'sqlite',

  pool: {
    max: 5,
    min: 0,
    acquire: 30000,
    idle: 10000
  },

  // SQLite only
  storage: './db/test.sqlite',

  // http://docs.sequelizejs.com/manual/tutorial/querying.html#operators
  operatorsAliases: false,

  // No log to avoid to keep test output clean
  // logging: console.log
  logging: false
});
